                if (phoneNumber) {
                  Linking.openURL(`tel:${phoneNumber}`);
                }
              }}
            >
              <AntDesign name="phone" size={24} color="rgba(3, 29, 68, 1)" />
            </TouchableOpacity>
          </View>
        </View>
        <View style={styles.formContainer}>
          <TextInput
            style={styles.input}
            placeholder="Name"
            value={name}
            onChangeText={(text) => {
              setName(text);
              setNameError("");
            }}
            onBlur={validateName}
          />
          {nameError ? <Text style={styles.errorText}>{nameError}</Text> : null}
          <TextInput
            style={styles.input}
            placeholder="CNIC"
            keyboardType="numeric"
            value={cnic}
            onChangeText={(text) => {
              setCnic(text);
              setCnicError("");
            }}
            // onBlur={validateCNIC}
            maxLength={13}
          />
          {cnicError ? <Text style={styles.errorText}>{cnicError}</Text> : null}
          <TextInput
            style={styles.input}
            placeholder="Email"
            value={email}
            onChangeText={(text) => {
              setEmail(text);
              setEmailError("");
            }}
            onBlur={validateEmail}
          />
          {emailError ? (
            <Text style={styles.errorText}>{emailError}</Text>
          ) : null}
          <View style={styles.phonePickerContainer}>
            <TextInput
              style={[styles.countryCodeInput, styles.input]}
              placeholder="PK"
              value={countryCode}
              editable={false}
            />
            <View style={styles.pickerContainer}>
              <Picker
                selectedValue={countryCode}
                onValueChange={(itemValue) => {
                  setCountryCode(itemValue);
                }}
                itemStyle={styles.pickerItem}
              >
                {countryCodes.map((code) => (
                  <Picker.Item
                    key={code}
                    label={code}
                    value={code}
                    style={
                      countryCode === code
                        ? styles.SelectedPickerItem
                        : styles.SelectedPickerItem1
                    }
                  />
                ))}
              </Picker>
            </View>
            <TextInput
              style={styles.phoneInput}
              placeholder="Phone Number"
              value={phoneNumber}
              onChangeText={(text) => {
                setPhoneNumber(text);
                setPhoneNumberError("");
              }}
              onBlur={validatePhoneNumber}
              keyboardType="numeric"
              maxLength={15}
            />
          </View>
          {phoneNumberError ? (
            <Text style={styles.errorText}>{phoneNumberError}</Text>
          ) : null}

          <Modal
            animationType="slide"
            transparent={true}
            visible={isFullImageModalVisible}
            onRequestClose={() => setFullImageModalVisible(false)}
          >
            <View style={styles.imageModalContainer}>
              <View style={styles.fullImageContainer}>
                {profileImageLink && (
                  <Image
                    source={{ uri: profileImageLink }}
                    style={styles.fullImage}
                  />
                )}
              </View>
              <TouchableOpacity
                style={styles.imageModalButton2}
                onPress={() => setFullImageModalVisible(false)}
              >
                <Text style={styles.imageModalButtonText}>Close</Text>
              </TouchableOpacity>
            </View>
          </Modal>

          <Modal
            animationType="slide"
            transparent={true}
            visible={isImageModalVisible}
            onRequestClose={() => setImageModalVisible(false)}
          >
            <View style={styles.imageModalContainer}>
              {/* Background Close Button */}
              <TouchableOpacity
                onPress={() => setImageModalVisible(false)}
                style={styles.closeButton}
              >
                <AntDesign
                  name="closecircle"
                  size={30}
                  color="rgba(3, 29, 68, 1)"
                />
              </TouchableOpacity>

              {/* Content */}
              <View style={styles.imageModalContent}>
                {/* Image Source Options */}
                <TouchableOpacity
                  style={styles.imageModalButton}
                  onPress={handleImageFromCamera}
                >
                  <Text style={styles.imageModalButtonText}>Take a Photo</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={styles.imageModalButton1}
                  onPress={handleImageFromGallery}
                >
                  <Text style={styles.imageModalButtonText}>
                    Choose from Gallery
                  </Text>
                </TouchableOpacity>
              </View>
            </View>
          </Modal>

          <View style={styles.buttonContainer}>
            <TouchableOpacity style={styles.button} onPress={handleSave}>
              <Text style={styles.buttonText}>Save</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </ImageBackground>
  );
};

