import React, { useCallback } from "react";
import { Image } from "expo-image";
import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  ActivityIndicator,
  Dimensions,
  Pressable,
  TouchableOpacity,
} from "react-native";
import { LinearGradient } from "expo-linear-gradient";
import { useNavigation } from "@react-navigation/native";
import Footer from "../components/Footer";
import { printToFileAsync } from "expo-print";
import * as FileSystem from "expo-file-system";
import { shareAsync } from "expo-sharing";
import * as Sharing from "expo-sharing";
import { FontFamily, Border, Color, FontSize, Padding } from "../GlobalStyles";
import { useRoute } from "@react-navigation/native";
import { useState, useEffect, useMemo } from "react";
import axios from "axios";
import Config from "./Config";
import AsyncStorage from "@react-native-async-storage/async-storage";
function InvoiceDetailView() {
  const route = useRoute();

  const invoiceId = route.params?.recordId;
  const [Invoice, setInvoice] = useState([]);

  const navigation = useNavigation();
  const [invoiceID, setInvoiceID] = useState("");
  const [date, setDate] = useState("");
  const [due, setDue] = useState("");
  const [balance, setBalance] = useState("");
  const [name, setName] = useState("");
  const [registrationNumber, setRegistrationNumber] = useState("");
  const [status, setStatus] = useState("");
  const [subTotal, setSubTotal] = useState("0");
  const [tax, setTax] = useState("");
  const [discount, setDiscount] = useState("");
  const [total, setTotal] = useState("");
  const [balanceDue, setBalanceDue] = useState("");
  const [description, setDescription] = useState([]);
  const [taxr, setTaxr] = useState([]);
  const [discountr, setDiscountr] = useState([]);
  const [vehicle, setVehicle] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const screenHeight = Dimensions.get("window").height;
  const screenWidth = Dimensions.get("window").width;
  const [businessProfile, setBusinessProfile] = useState("");
  const [currency, setCurrency] = useState("");
  useEffect(() => {
    getData();
    getProfileImage();
  }, [invoiceId]);
  useEffect(() => {
    calculateTotalAmount();
  });
  const rem = screenWidth / 16;

  const getData = async () => {
    setIsLoading(true);
    let token = await AsyncStorage.getItem("accessToken");
    const accessToken = "Bearer " + token;

    let config = {
      method: "get",
      maxBodyLength: Infinity,
      url: `${Config.apiServerUrl}/api/invoice/get-invoice/${invoiceId}`,
      headers: {
        Authorization: accessToken,
      },
    };

    axios
      .request(config)
      .then((response) => {
               const dateObj = new Date(response.data[0].date);
        const year = dateObj.getFullYear();
        const month = dateObj.getMonth() + 1;
        const day = dateObj.getDate();
        setDate(`${month}/${day}/${year}`);
        setDue(response.data[0].invoiceDue);
        setInvoiceID(response.data[0].id);
        setTotal(response.data[0].total);
        setDescription(response.data[0].descriptions);
        setTaxr(response.data[0].taxes);
        setDiscountr(response.data[0].discounts);
        setName(response.data[0].name);
        setRegistrationNumber(response.data[0].registrationNumber);
        setVehicle(response.data[0].vehicleName);
        setCurrency(response.data[0].currency);
        let st = response.data[0].status;
        setStatus(st ? "Paid" : "Due");
        setBalance(st ? 0 : response.data[0].total);
        calculateTotalAmount();
        setIsLoading(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getProfileImage = async () => {
    try {
      const accessTokens = await AsyncStorage.getItem("accessToken");
      const token = "Bearer " + accessTokens;
      const Business_id = await AsyncStorage.getItem("Business_id");

      if (Business_id) {
    
        const config = {
          method: "get",
          maxBodyLength: Infinity,
          url: `${Config.apiServerUrl}/api/business/${Business_id}/profile-image`,
          headers: {
            Authorization: token,
          },
        };

        const response = await axios.request(config);

        if (response.status === 200) {
          const responseData = response.data;

          setBusinessProfile(`${Config.baseUrl1}` + responseData.url);
            } else {
          console.log("Error: " + response.statusText);
        }
      }
    } catch (error) {
      console.log("Error fetching profile image:", error);
    }
  };

  const calculateTotalAmount = () => {
    setDiscount(0);
    setTax(0);
    if (
      description.length === 0 &&
      discountr.length === 0 &&
      taxr.length === 0
    ) {
      setTotal(0.0);
      return;
    }

    let totalAmount = 0.0;

    for (const item of description) {
      const itemAmount = parseFloat(item.quantity) * parseFloat(item.rate);
      totalAmount += isNaN(itemAmount) ? 0 : itemAmount;
    }

    setSubTotal(totalAmount);

    let totalDiscount = 0.0;
    for (const disc of discountr) {
      const discountRate = parseFloat(disc.discountRate);
      if (!isNaN(discountRate)) {
        totalDiscount += (discountRate / 100) * totalAmount; // Convert rate to decimal
      }
    }

    setDiscount(totalDiscount);

    let totaltax = 0.0;
    for (const t of taxr) {
      const taxRate = parseFloat(t.taxRate);

      if (!isNaN(taxRate)) {
        totaltax += (taxRate / 100) * totalAmount; // Convert rate to decimal
      }
    }
    setTax(totaltax);

    totalAmount -= totalDiscount;
    totalAmount += totaltax;

    totalAmount = totalAmount.toFixed(2);
    setTotal(totalAmount);
  };

  const descriptionRows = description
    .map(
